import React, { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { userContext } from "../../../App";

const BookingList = () => {
  const [loggedInUser, setLoggedInUser] = useContext(userContext);
  const [matchedBooking, setMatchedBooking] = useState([]);

  const userEmail = loggedInUser.email;
  console.log(userEmail);

  useEffect(() => {
    fetch(`http://localhost:5000/booking/${userEmail}`)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setMatchedBooking(data);
      });
  }, [userEmail]);
  return (
    <div>
      <h2 className="container">Your Order Count: {matchedBooking.length}</h2>

      <table className="container table table-borderless">
        <thead>
          <tr className="border-bottom">
            <th>Booked Service</th>
            <th>Name</th>
            <th>Email</th>
            <th>Address</th>
            <th>Price</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {matchedBooking.map((booking) => (
            <tr>
              <td>{booking.service}</td>
              <td>{booking.name}</td>
              <td>{booking.email}</td>
              <td>{booking.address}</td>
              <td>{booking.price}</td>
              <td>P</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default BookingList;
