import React, { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { userContext } from "../../../App";

const BookingList = () => {
  const [loggedInUser, setLoggedInUser] = useContext(userContext);
  const [matchedBooking, setMatchedBooking] = useState([]);

  const userEmail = loggedInUser.email;
  console.log(userEmail);

  useEffect(() => {
    fetch(`http://localhost:5000/booking/${userEmail}`)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setMatchedBooking(data);
      });
  }, [userEmail]);
  return (
    <div>
      <h2 className="container">Your Order Count: {matchedBooking.length}</h2>

      <table className="container table table-borderless">
        <thead>
          <tr className="border-bottom">
            <th>Email</th>
            <th>Description</th>
            <th>Quantity</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {matchedBooking.map((product) => (
            <tr className="border-bottom">
              <td>{product.email}</td>
              <td>{product.description}</td>
              <td>{product.quantity}</td>
              <td>{parseInt(product.price)}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <Link to="/shipment">
        <button className="btn btn-primary ml-5">Shipment</button>
      </Link>
    </div>
  );
};

export default BookingList;
