import React, { useEffect, useState } from "react";
import BookingDetailsCard from "../BookingDetailsCard/BookingDetailsCard";
import "./BookingDetails.css";

const BookingDetails = ({ bookingDate }) => {
  const [bookingData,setBookingData]=useState([])
  useEffect(() => {
    fetch("http://localhost:5000/bookingData")
      .then((res) => res.json())
      .then((result) => setBookingData(result))
      .catch((err) => console.log(err));
  }, []);
  // const bookingData = [
  //   {
  //     cloths: 6,
  //     title: "Dry Cleaning / Launder & Press",
  //     shirt: 2,
  //     pants: 2,
  //     sweaters: 2,
  //     price: 10,
  //   },
  //   {
  //     cloths: 12,
  //     title: "Wash & Fold",
  //     shirt: 3,
  //     pants: 3,
  //     sweaters: 2,
  //     price: 20,
  //   },
  //   {
  //     cloths: 20,
  //     title: "Hang Dry",
  //     shirt: 4,
  //     pants: 4,
  //     sweaters: 2,
  //     price: 50,
  //   },
  // ];
  return (
    <div>
      <div>
        <div>
          <h2
            style={{ color: "#1cc7c1", fontWeight: "bold" }}
            className="text-center mb-3 mt-4"
          >
            You Have Choosed Booking at {bookingDate.toDateString()}
          </h2>
        </div>
        <div className="container">
          <div className="row">
            {bookingData.map((booking) => (
              <BookingDetailsCard
                booking={booking}
                date={bookingDate}
              ></BookingDetailsCard>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default BookingDetails;
