import React from "react";
import ServiceCard from "../ServiceCard/ServiceCard";
import './OurService.css'

const OurServices = () => {
  const serviceData = [
    {
      cloths: 6,
      title: "Dry Cleaning / Launder & Press",
      shirt: 2,
      pants: 2,
      sweaters: 2,
      price: 10,
    },
    {
      cloths: 12,
      title: "Wash & Fold",
      shirt: 3,
      pants: 3,
      sweaters: 2,
      price: 20,
    },
    {
      cloths: 20,
      title: "Hang Dry",
      shirt: 4,
      pants: 4,
      sweaters: 2,
      price: 50,
    },
  ];
  return (
    <div>
      <div>
        <h1 className='text-center mb-3 mt-4'>Dry Cleaning and Laundry Made Simple</h1>
        <p className="underline container w-25 mt-5"></p>
        <h6 className='text-center mt-4 mb-5' style={{color: "#1cc7c1",fontWeight:'bold' }}>HOW OUR SERVICE WORKS:</h6>
      </div>
      <div className='container'>
        <div className="row">
          {serviceData.map((service) => (
            <ServiceCard service={service}></ServiceCard>
          ))}
        </div>
      </div>
    </div>
  );
};

export default OurServices;
