import React, { useEffect, useState } from "react";
import { Button, Col, Form } from "react-bootstrap";

const AddBooking = () => {
  const [userBooking, setUserBooking] = useState([]);
  const [bookingInfo, setBookingInfo] = useState({});
  const [showPrice,setShowPrice]=useState(null)
  console.log(bookingInfo);
    const matchedService=userBooking.filter(title => title.title === bookingInfo.service);
    console.log(matchedService[0]?.price);
  const handleBlur = (e) => {
    const newInfo = { ...bookingInfo };
    newInfo[e.target.name] = e.target.value;
    setBookingInfo(newInfo);
  };
  const handleSubmit = (e) => {
    e.preventDefault();
  };
  useEffect(() => {
    fetch("http://localhost:5000/bookingData")
      .then((res) => res.json())
      .then((data) => setUserBooking(data))
      .catch((err) => console.log(err));
  }, []);
  return (
    <div className="w-50">
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="exampleInputEmail1">Select Service</label>
          <select
            onBlur={handleBlur}
            type="select"
            className="form-control"
            name="service"
            placeholder="Enter Your Name"
          >
          <option value=''>..Select</option>
          {userBooking.map(book => <option value={book.value}>{book.title}</option>)}
            <option value={userBooking.title}></option>
          </select>
        </div>
        <p>Price for Selected Service: <b> ${matchedService[0]?.price} </b></p>
        <div className="form-group">
          <label htmlFor="exampleInputEmail1">Name</label>
          <input
            onBlur={handleBlur}
            type="text"
            className="form-control"
            name="name"
            placeholder="Enter Your Name"
          />
        </div>
        <div className="form-group">
          <label htmlFor="exampleInputPassword1">Email</label>
          <input
            onBlur={handleBlur}
            type="email"
            className="form-control"
            name="email"
            placeholder="Enter Your Email"
          />
        </div>
        <div className="form-group">
          <label htmlFor="exampleInputPassword1">Address</label>
          <input
            onBlur={handleBlur}
            type="text"
            className="form-control"
            name="address"
            placeholder="Input Address"
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
    </div>
  );
};

export default AddBooking;
