import React, { useContext, useState } from "react";
import firebase from "firebase/app";
import "firebase/auth";
import firebaseConfig from "./firebase.config";
import loginBg from "../../../images/login.jpg";
import logo from "../../../images/logo.png";
import { useHistory, useLocation } from "react-router";
import { Link } from "react-router-dom";
import { userContext } from "../../../App";

const Login = () => {


  const [email, setEmail] = useState();
  console.log(email);
  const [signInUser,setSignInUser]=useContext(userContext);
  const [password, setPassword] = useState();
  const history = useHistory();
  let location = useLocation();
  let { from } = location.state || { from: { pathname: "/" } };

  console.log(signInUser);
  const handleLoginMatch = (e) => {
    fetch("http://localhost:5000/user?email=" + email)
    .then((res) => {
      if (res.status === 200) {
        res.json()
        .then((result) => {
          if (result.email) {
            console.log(result.email);
              setSignInUser(result);
              sessionStorage.setItem('token',result.email)
              history.replace(from);
            localStorage.setItem("email", email);
          } else {
            // history.replace("/");
          }
        });
      }
    });
    e.preventDefault();
  };
  return (
    <div className="mt-4">
    <form className="w-50 card m-auto p-4">
      <h3>Sign In</h3>

      <div className="form-group">
        <label>Email address</label>
        <input
          onChange={(e) => setEmail(e.target.value)}
          type="email"
          className="form-control"
          placeholder="Enter email"
        />
      </div>

      <div className="form-group">
        <label>Password</label>
        <input
          onChange={(e) => setPassword(e.target.value)}
          type="password"
          className="form-control"
          placeholder="Enter password"
        />
      </div>

      <div className="form-group">
        <div className="custom-control custom-checkbox">
          <input
            type="checkbox"
            className="custom-control-input"
            id="customCheck1"
          />
          <label className="custom-control-label" htmlFor="customCheck1">
            Remember me
          </label>
        </div>
      </div>

      <button
        onClick={handleLoginMatch}
        type="submit"
        className="btn btn-primary btn-block"
      >
        Submit
      </button>
      <p className="forgot-password text-right mt-2">
        Don't Have Account? <Link to="/signup">Create Account</Link>
      </p>
    </form>
    <p style={{color: 'red'}}>You can Create Account or use demo(email: rian.acca@gmail.com,password: 123456) </p>
  </div>
  );
};

export default Login;
