import React, { useState } from "react";
import { Button, Card } from "react-bootstrap";
import "./BookingDetailsCard.css";
import service from "../../../images/mainhead.png";
import { Link } from "react-router-dom";
import BookingForm from "../BookingForm/BookingForm";

const BookingDetailsCard = (props) => {
  const { title, cloths, pants, price, shirt, sweaters } = props.booking;
  const [modalIsOpen, setIsOpen] = useState(false);
    function openModal() {
      setIsOpen(true);
    }
  function closeModal() {
    setIsOpen(false);
  }
  return (
    <div className="col-md-4">
      <Card style={{ width: "20rem" }} className="text-center">
        <Card.Img variant="top" src={service} />
        <Card.Body>
          <Card.Text style={{ color: "#1cc7c1" }}>{cloths} cloths</Card.Text>
          <Card.Title>{title}</Card.Title>
          <Card.Text className="container">
            <p className="card p-2 text-secondary">Pants: {pants}</p>
            <p className="text-secondary">Shirts: {shirt}</p>
            <p className="card p-2 text-secondary">Sweaters: {sweaters}</p>
          </Card.Text>
          <Card.Title style={{ color: "#1cc7c1", fontWeight: "bold" }}>
            ${price}
          </Card.Title>
          <Link to="/bookings">
            <Button onClick={openModal} variant="primary">
              Book Service
            </Button>
            <BookingForm
              modalIsOpen={modalIsOpen}
              closeModal={closeModal}
              serviceTitle={title}
            ></BookingForm>
          </Link>
        </Card.Body>
      </Card>
    </div>
  );
};

export default BookingDetailsCard;
