import React, { useState } from "react";
import { Col, Form } from "react-bootstrap";
import { useForm } from "react-hook-form";
const axios = require("axios");

const AddService = () => {
  const [image, setImage] = useState(null);
  const [info, setInfo] = useState({});
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm();
  const handleFileUpload = (event) => {
    // setImage(event.target.files[0]);
    const imageData = new FormData();
    imageData.set("key", "8bf8ebc2ca539bc22b4cfe63af75b480");
    imageData.append("image", event.target.files[0]);
    axios
      .post("https://api.imgbb.com/1/upload", imageData)
      .then(function (response) {
        setImage(response.data.data.display_url);
      })
      .catch(function (error) {
        console.log(error);
      });
  };
  const onSubmit = (data) => {
data.image=image
    fetch("http://localhost:5000/addAdminService", {
      method: "POST",
      headers: {'Content-type': 'application/json'},
      body: JSON.stringify(data)
    })
      .then((res) => res.json())
      .then((result) => {
        console.log(result);
      });
  };
  return (
    <div className="w-50">
      <form onSubmit={handleSubmit(onSubmit)}>
        <label htmlFor="title">Service Title</label>
        <input
          className="form-control"
          type="text"
          defaultValue=""
          {...register("title")}
        />
        <label htmlFor="cloths">Cloths</label>
        <input
          className="form-control"
          type="text"
          {...register("cloths", { required: true })}
        />
        <label htmlFor="shirt">Shirt</label>
        <input
          className="form-control"
          type="text"
          {...register("shirt", { required: true })}
        />
        <label htmlFor="pants">Pant</label>
        <input
          className="form-control"
          type="text"
          {...register("pants", { required: true })}
        />
        <label htmlFor="sweaters">sweaters</label>
        <input
          className="form-control"
          type="text"
          {...register("sweaters", { required: true })}
        />
        <label htmlFor="price">Price</label>
        <input
          className="form-control"
          type="text"
          {...register("price", { required: true })}
        />
        <label htmlFor="image">Upload Service Image</label>
        <input
          className="form-control"
          onChange={handleFileUpload}
          type="file"
          {...register("image", { required: true })}
        />
        <br />
        <input className="btn btn-danger" type="submit" />
      </form>
    </div>
  );
};

export default AddService;
