import React, { useEffect, useState } from "react";
import { Button, Col, Form } from "react-bootstrap";

const AddBooking = () => {
  const [userBooking, setUserBooking] = useState([]);
  const [bookingInfo, setBookingInfo] = useState({});
  const handleBlur = (e) => {
    const newInfo = { ...bookingInfo };
    newInfo[e.target.name] = e.target.value;
    setBookingInfo(newInfo);
  };
  const handleSubmit = () => {};
  useEffect(() => {
    fetch("http://localhost:5000/bookingData")
      .then((res) => res.json())
      .then((data) => setUserBooking(data))
      .catch((err) => console.log(err));
  }, []);
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="exampleInputEmail1">Select Service</label>
          <select
            onBlur={handleBlur}
            type="select"
            className="form-control"
            name="name"
            placeholder="Enter Your Name"
          >
            <option value="">{userBooking.title}</option>
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="exampleInputEmail1">Name</label>
          <input
            onBlur={handleBlur}
            type="text"
            className="form-control"
            name="name"
            placeholder="Enter Your Name"
          />
        </div>
        <div className="form-group">
          <label htmlFor="exampleInputPassword1">Email</label>
          <input
            onBlur={handleBlur}
            type="email"
            className="form-control"
            name="email"
            placeholder="Enter Your Email"
          />
        </div>
        <div className="form-group">
          <label htmlFor="exampleInputPassword1">Address</label>
          <input
            onBlur={handleBlur}
            type="text"
            className="form-control"
            name="address"
            placeholder="Input Address"
          />
        </div>
        <div className="form-group">
          <label htmlFor="exampleInputPassword1">Shirts</label>
          <input
            onBlur={handleBlur}
            type="text"
            className="form-control"
            name="shirts"
            placeholder="Number Of Shirts"
          />
        </div>
        <div className="form-group">
          <label htmlFor="exampleInputPassword1">Sweaters</label>
          <input
            onBlur={handleBlur}
            type="text"
            className="form-control"
            name="sweaters"
            placeholder="Number Of Sweaters"
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
    </div>
  );
};

export default AddBooking;
