import React from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import "./Dashboard.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCog,
  faSignOutAlt,
  faCalendar,
  faGripHorizontal,
  faUsers,
  faUserPlus,
} from "@fortawesome/free-solid-svg-icons";
import { faFileAlt } from "@fortawesome/free-regular-svg-icons";

const routes = [
  {
    path: "/",
    exact: true,
    sidebar: () => <div></div>,
    main: () => <h2>Home</h2>,
  },
  {
    path: "/bubblegum",
    sidebar: () => <div></div>,
    main: () => <h2>Bubblegum</h2>,
  },
  {
    path: "/shoelaces",
    sidebar: () => <div></div>,
    main: () => <h2>Shoelaces</h2>,
  },
];

const Dashboard = () => {
  return (
    <Router>
      <div>
        <div
          className="sidebar d-flex flex-column justify-content-between py-5 px-4"
          style={{ height: "100vh" }}
        >
          <ul className="list-unstyled">
            <li>
              <Link to="/dashboard" className="text-white">
                <FontAwesomeIcon icon={faGripHorizontal} />{" "}
                <span>Dashboard</span>
              </Link>
            </li>
            {/* //User Part */}
            <li>
              <Link to="/dashboard/booking" className="text-white">
                <FontAwesomeIcon icon={faCalendar} /> <span>Book</span>
              </Link>
            </li>
            <li>
              <Link to="/dashboard/allbooking" className="text-white">
                <FontAwesomeIcon icon={faUsers} /> <span>Booking List</span>
              </Link>
            </li>
            <li>
              <Link to="/dashboard/allbooking" className="text-white">
                <FontAwesomeIcon icon={faUsers} /> <span>Review</span>
              </Link>
            </li>
            {/* //Admin Part */}
            <li>
              <Link to="/dashboard/allbooking" className="text-white">
                <FontAwesomeIcon icon={faUsers} /> <span>Order List</span>
              </Link>
            </li>
            <li>
              <Link to="/dashboard/allbooking" className="text-white">
                <FontAwesomeIcon icon={faUsers} /> <span>Add Service</span>
              </Link>
            </li>
            <li>
              <Link to="/dashboard/allbooking" className="text-white">
                <FontAwesomeIcon icon={faUsers} /> <span>Make Admin</span>
              </Link>
            </li>
            <li>
              <Link to="/dashboard/allbooking" className="text-white">
                <FontAwesomeIcon icon={faUsers} /> <span>Manage Service</span>
              </Link>
            </li>
          </ul>
          <div>
            <Link to="/" className="text-white">
              <FontAwesomeIcon icon={faSignOutAlt} /> <span>Logout</span>
            </Link>
          </div>
          <Switch>
            {routes.map((route, index) => (
              <Route
                key={index}
                path={route.path}
                exact={route.exact}
                children={<route.sidebar />}
              />
            ))}
          </Switch>
        </div>

        <div style={{ flex: 1, padding: "10px" }}>
          <Switch>
            {routes.map((route, index) => (
              <Route
                key={index}
                path={route.path}
                exact={route.exact}
                children={<route.main />}
              />
            ))}
          </Switch>
        </div>
      </div>
    </Router>
  );
};

export default Dashboard;
