import React, { useState } from "react";
import { Col, Form } from "react-bootstrap";
import { useForm } from "react-hook-form";

const AddService = () => {
  const [file, setFile] = useState(null);
  const [info, setInfo] = useState({});
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm();
  const handleFileUpload = (e) => {
    const newFile = e.target.files[0];
    setFile(newFile);
  };
  const onSubmit = (data) => {
    setInfo(data);
    const formData = new FormData();
    formData.append("file", file);
    formData.append("info", info);
    console.log(data);
    fetch("http://localhost:5000/addAdminService", {
      method: "POST",
      body: formData,
    })
      .then((res) => res.json())
      .then((result) => {
        console.log(result);
      });
  };
  return (
    <div className="w-50">
      <form onSubmit={handleSubmit(onSubmit)}>
        <label htmlFor="title">Service Title</label>
        <input
          className="form-control"
          type="text"
          defaultValue=""
          {...register("title")}
        />
        <label htmlFor="cloths">Cloths</label>
        <input
          className="form-control"
          type="text"
          {...register("cloths", { required: true })}
        />
        <label htmlFor="shirt">Shirt</label>
        <input
          className="form-control"
          type="text"
          {...register("shirt", { required: true })}
        />
        <label htmlFor="pants">Pant</label>
        <input
          className="form-control"
          type="text"
          {...register("pants", { required: true })}
        />
        <label htmlFor="sweaters">sweaters</label>
        <input
          className="form-control"
          type="text"
          {...register("sweaters", { required: true })}
        />
        <label htmlFor="price">Price</label>
        <input
          className="form-control"
          type="text"
          {...register("price", { required: true })}
        />
        <label htmlFor="image">Upload Service Image</label>
        <input
          className="form-control"
          onChange={handleFileUpload}
          type="file"
          {...register("image", { required: true })}
        />
        <br />
        <input className="btn btn-danger" type="submit" />
      </form>
    </div>
  );
};

export default AddService;
